+    const tooltip = page.locator(TOOLTIP_SELECTOR);
+    // There should be no tooltip yet
+    await expect(tooltip).toHaveCount(0);
+    // Invoke the tooltip
+    await page.keyboard.press('Shift+Tab');
+    // There should be a tooltip now
