new file mode 100644
index 0000000000..10cb1d08e3
--- /dev/null
+++ b/packages/docs/docs/api/fhir/operations/patient-set-accounts.md
@@ -0,0 +1,72 @@
+---
+sidebar_position: 5.1
+---
+
+# Patient $set-accounts Operation
+
+Medplum implements a custom `$set-accounts` operation for Patient resources to manage account references. __This is the recommended way to manage account references for patients__.
+
+This operation sets the Patient's _meta.accounts_ references and propogates them to the resources in that patient's compartment. This is useful when you need to ensure consistent _meta.accounts_ access across all resources related to a patient.
+
+:::warning
+
+This operation will only update the first 1,000 resources in the patient's compartment.
+
+:::
+
+For example, when this operation is used to add a new organization to a patient's _meta.accounts_, it will update both the patient and all resources in the patient's compartment to include that organization in each of their _meta.accounts_ list. Then, by updating the references in _meta.accounts_, each resource's _meta.compartment_ will also be refreshed to include the references in _meta.accounts_.
+
+:::note
+
+Resources in a patient's compartment are defined by the [FHIR Patient CompartmentDefinition](https://hl7.org/fhir/R4/compartmentdefinition-patient.html). This includes resources where the patient is the _subject_, as well as resources that are directly linked to the patient through specific references (_performer_, _author_, _participant_, etc.).
+
+:::
+
+## Invoke the `$set-accounts` operation
+
+```
+POST [base]/R4/Patient/<id>/$set-accounts
+```
+
+### Input
+The input is a [FHIR Parameters](/docs/api/fhir/resources/parameters) resource containing:
+- `accounts` a reference to set in each resource's _meta.accounts_
+
+Example request payload:
+```json
+{
+  "resourceType": "Parameters",
+  "parameter": [
+    {
+      "name": "accounts",
+      "valueReference": {
+        "reference": "Organization/<organization-id>"
+      }
+    },
+    {
+      "name": "accounts",
+      "valueReference": {
+        "reference": "Practitioner/<practitioner-id>"
+      }
+    }
+  ]
+}
+```
+
+### Output
+
+The output is a [FHIR Parameters](/docs/api/fhir/resources/parameters) resource containing:
+- `resourcesUpdated` The number of resources that were updated
+
+Example response if patient has 3 resources in their compartment:
+```json
+{
+  "resourceType": "Parameters",
+  "parameter": [
+    {
+      "name": "resourcesUpdated",
+      "valueInteger": 3
+    }
+  ]
+}
+```
\ No newline at end of file

diff --git a/packages/server/src/fhir/operations/patientsetaccounts.ts b/packages/server/src/fhir/operations/patientsetaccounts.ts
new file mode 100644
index 0000000000..a7df77a201
--- /dev/null
+++ b/packages/server/src/fhir/operations/patientsetaccounts.ts
@@ -0,0 +1,104 @@
+import { allOk, badRequest, forbidden } from '@medplum/core';
+import { FhirRequest, FhirResponse } from '@medplum/fhir-router';
+import { Patient, Reference, OperationDefinition } from '@medplum/fhirtypes';
+import { getAuthenticatedContext } from '../../context';
+import { getPatientEverything } from './patienteverything';
+import { parseInputParameters, buildOutputParameters } from './utils/parameters';
+
+const operation: OperationDefinition = {
+  resourceType: 'OperationDefinition',
+  id: 'patient-set-accounts',
+  name: 'SetAccounts',
+  status: 'active',
+  kind: 'operation',
+  code: 'set-accounts',
+  description: "Updates account references for all resources in the patient's compartment",
+  resource: ['Patient'],
+  system: false,
+  type: false,
+  instance: true,
+  parameter: [
+    {
+      // Input parameter for the accounts array
+      use: 'in',
+      name: 'accounts',
+      type: 'Reference',
+      min: 0,
+      max: '*',
+      documentation: 'List of account references to set',
+    },
+    {
+      // Output parameter for number of resources updated
+      use: 'out',
+      name: 'resourcesUpdated',
+      type: 'integer',
+      min: 1,
+      max: '1',
+      documentation: 'Number of resources that were updated',
+    },
+  ],
+};
+
+export interface PatientSetAccountsParameters {
+  accounts: Reference[];
+}
+
+/**
+ * Handles the Patient $set-accounts operation.
+ * This operation updates the account reference for all resources in the patient compartment.
+ * The operation only updates resources from the getPatientEverything bundle that are not
+ * included in the patient's compartment, but are linked to the patient through a reference.
+ *
+ * Note: the operation is currently capped at 1000 resources in the patient's compartment.
+ * @param req - The FHIR request.
+ * @returns The FHIR response.
+ */
+export async function patientSetAccountsHandler(req: FhirRequest): Promise<FhirResponse> {
+  const { id } = req.params;
+  if (!id) {
+    return [badRequest('Must specify Patient ID')];
+  }
+
+  const params = parseInputParameters<PatientSetAccountsParameters>(operation, req);
+  const ctx = getAuthenticatedContext();
+
+  const isSuperAdmin = ctx.repo.isSuperAdmin();
+  if (!ctx.repo.isProjectAdmin() && !isSuperAdmin) {
+    return [forbidden];
+  }
+
+  const patient = await ctx.repo.readResource<Patient>('Patient', id);
+  const bundle = await getPatientEverything(ctx.repo, patient);
+  const accounts = params.accounts;
+
+  // step 1: update the patient resource with the new accounts
+  patient.meta = {
+    ...patient.meta,
+    accounts: accounts,
+    account: accounts?.[0],
+  };
+
+  await ctx.repo.updateResource(patient);
+
+  // step 2: update the resources in the patient compartment to trigger meta.accounts refresh
+  let count = 1;
+  for (const entry of bundle.entry ?? []) {
+    if (entry.search?.mode === 'match') {
+      const resource = entry.resource;
+      if (resource && resource.resourceType !== 'Patient') {
+        delete resource.meta?.accounts;
+        delete resource.meta?.account;
+
+        await ctx.repo.updateResource(resource);
+        count++;
+      }
+    }
+  }
+
+  return [
+    allOk,
+    buildOutputParameters(operation, {
+      resourcesUpdated: count,
+    }),
+  ];
+}

