-            switch (fromPrimitive) {
-                case Void:
-                case None:
-                case Null:
-                case String:
-                    return false;
-                case Boolean:
-                    return fromPrimitive == to;
-                default:
-                    switch (to) {
-                        case Byte:
-                        case Char:
