-                            String description = extractValue(doc, "description");
-                            if (displayName != null && description != null) {
-                                recipeSource.insertRow(ctx, new RewriteRecipeSource.Row(
-                                        displayName,
-                                        description,
-                                        RewriteRecipeSource.RecipeType.Yaml,
