+    static class RegexReplaceVisitor<P> extends XmlVisitor<P> {
+
+        Xml.Tag scope;
+        String oldValue;
+        String newValue;
+
+        @Override
+        public Xml visitTag(Xml.Tag tag, P p) {
+            Xml.Tag t = (Xml.Tag) super.visitTag(tag, p);
+            if (scope.isScope(t) &&
+                    t.getContent() != null &&
+                    t.getContent().size() == 1 &&
+                    t.getContent().get(0) instanceof Xml.CharData) {
