import org.openrewrite.java.JavaIsoVisitor;
import org.openrewrite.java.JavaParser;
import org.openrewrite.java.JavaTemplate;
import org.openrewrite.java.tree.J;


public class AddAnnotation extends Recipe {
    private static final AnnotationMatcher SIZE = new AnnotationMatcher("@jakarta.validation.constraints.Size");

    @Override
    public String getDisplayName() {
        return "Add validation annotations";
    }

    @Override
    public String getDescription() {
        return "something meaningful.";
    }

    @Override
    public TreeVisitor<?, ExecutionContext> getVisitor() {
        return new JavaIsoVisitor<ExecutionContext>() {
            @Override
            public J.MethodDeclaration visitMethodDeclaration(J.MethodDeclaration method, ExecutionContext ctx) {
                return method.withParameters(ListUtils.map(method.getParameters(), p -> {
                    J.VariableDeclarations declarations = (J.VariableDeclarations) p;
                    List<J.Annotation> annotations = declarations.getLeadingAnnotations();
                    if (!(annotations.stream().anyMatch(SIZE::matches))) {
                        maybeAddImport("jakarta.validation.constraints.Size");
                        return JavaTemplate.builder(" @Size(1) ")
                                .imports("jakarta.validation.constraints.Size").javaParser(JavaParser.fromJavaVersion().classpathFromResources(ctx, "jakarta.validation-api"))
                                .build().apply(
                                        new Cursor(getCursor(), declarations),
                                        declarations.getCoordinates().addAnnotation(Comparator.comparing(J.Annotation::getSimpleName))
                                );
                    } else return declarations;
                }));
            }

        };
    }
}
```

