--- a/rewrite-java/src/main/java/org/openrewrite/java/internal/TypesInUse.java
+++ b/rewrite-java/src/main/java/org/openrewrite/java/internal/TypesInUse.java
@@ -65,8 +65,11 @@ public J.Import visitImport(J.Import _import, Integer p) {
         @Override
         public J.Identifier visitIdentifier(J.Identifier identifier, Integer p) {
             Object parent = Objects.requireNonNull(getCursor().getParent()).getValue();
-            if (parent instanceof J.ClassDeclaration || parent instanceof J.MethodDeclaration) {
-                // skip type of class and method declaration name
+            if (parent instanceof J.ClassDeclaration) {
+                // skip type of class
+                return identifier;
+            } else if (parent instanceof J.MethodDeclaration && ((J.MethodDeclaration) parent).getName() == identifier) {
+                // skip method name
                 return identifier;
             }
             return super.visitIdentifier(identifier, p);

