+                    while (file != null && relativeSegments > 0) {
+                        relativeSegments--;
+                        file = file.getParentFile();
+                    }
+                    if (file != null) {
+                        result.set(file.toPath());
+                    }
+                }
+            });
+            return result.get();
+        }
