+        RestAssured.given().contentType("text/plain").post("/greetings/Quarkus")
+                .then()
+                .statusCode(200)
+                .body(Matchers.equalTo("Hello Quarkus"));
+
+        RestAssured.given().contentType("application/xml").post("/greetings/Quarkus")
