+            return "Hello %s".formatted(name);
+        }
+
+        @POST
+        @Path("/{name}")
+        @Consumes(MediaType.APPLICATION_XML)
+        @Produces(MediaType.TEXT_PLAIN)
+        public String hello(@PathParam("name") List<String> name, String ignored) {
+            return "Hello %s, how are you?".formatted(name);
+        }
+    }
+}

