+    public VaadinSecurityConfigurer enableRequestCacheConfiguration(
+            boolean enableRequestCacheConfiguration) {
+        this.enableRequestCacheConfiguration = enableRequestCacheConfiguration;
+        return this;
+    }
+
+    /**
