+    public VaadinSecurityConfigurer enableAuthorizedRequestsConfiguration(
+            boolean enableAuthorizedRequestsConfiguration) {
+        this.enableAuthorizedRequestsConfiguration = enableAuthorizedRequestsConfiguration;
+        return this;
+    }
+
+    /**
