+            return RequestMatchers.anyOf(baseMatcher,
+                    // Matchers for known Hilla views
+                    getRequestUtil()::isAllowedHillaView,
+                    // Matcher for public Hilla endpoints
+                    getRequestUtil()::isAnonymousEndpoint);
