+            logoutSuccessUrl = mapping.replaceFirst("/\\*$", "/");
+        }
+        String contextPath = servletContext.getContextPath();
+        if (!"".equals(contextPath)) {
+            logoutSuccessUrl = contextPath + logoutSuccessUrl;
+        }
+        return logoutSuccessUrl;
+    }
